import React, { useState } from "react";
import BuscaDados from "./Components/BuscaDados";

function App() {
  const [ticket, setTicket] = useState("");

  const buscaDados = () => {
    if (ticket === "") {
      alert("Preencha os dados");
    }
    // Adicione aqui a lógica para buscar os dados com base no ticket
  };

  return (
    <div>
      <h1>Bem vindo</h1>
      <p>Ticket: </p>
      <input
        type="text"
        value={ticket}
        placeholder="Insira o ticket da ação"
        onChange={(e) => setTicket(e.target.value)}
      />
      <button onClick={buscaDados}>Buscar</button>

      {/* Renderize o componente BuscaDados diretamente */}
      <BuscaDados />
    </div>
  );
}

export default App;
